   309→    private fun applyNewBackgroundColor(recyclerView: RecyclerView) {
   310→        try {
   311→            val typedValue = android.util.TypedValue()
   312→            val theme = requireContext().theme
   313→            
   314→            // Try multiple background color attributes in order of preference
   315→            val backgroundAttrs = arrayOf(
   316→                com.omiyawaki.osrswiki.R.attr.paper_color,
   317→                com.google.android.material.R.attr.colorSurface,
   318→                android.R.attr.colorBackground
   319→            )
   320→            
   321→            for (attr in backgroundAttrs) {
   322→                if (theme.resolveAttribute(attr, typedValue, true)) {
   323→                    // Apply to RecyclerView
   324→                    recyclerView.setBackgroundColor(typedValue.data)
   325→                    
   326→                    // CRITICAL: Also apply to parent views to ensure complete background coverage
   327→                    applyBackgroundToParentViews(recyclerView, typedValue.data)
   328→                    
   329→                    L.d("AppearanceSettingsFragment: Applied background color from ${getAttributeName(attr)}: ${Integer.toHexString(typedValue.data)}")
   330→                    break
   331→                }
   332→            }
   333→        } catch (e: Exception) {
