   337→    
   338→    private fun applyBackgroundToParentViews(view: android.view.View, backgroundColor: Int) {
   339→        try {
   340→            var currentView = view.parent
   341→            var level = 0
   342→            val maxLevels = 3 // Limit to prevent going too far up the hierarchy
   343→            
   344→            while (currentView != null && currentView is android.view.View && level < maxLevels) {
   345→                val parentView = currentView as android.view.View
   346→                
   347→                // Apply background to suitable parent views
   348→                if (shouldApplyBackgroundToParent(parentView)) {
   349→                    parentView.setBackgroundColor(backgroundColor)
   350→                    L.d("AppearanceSettingsFragment: Applied background to parent view level $level: ${parentView.javaClass.simpleName}")
   351→                }
   352→                
   353→                currentView = parentView.parent
   354→                level++
   355→            }
   356→        } catch (e: Exception) {
   357→            L.w("AppearanceSettingsFragment: Error applying background to parent views: ${e.message}")
   358→        }
   359→    }
   360→    
   361→    private fun shouldApplyBackgroundToParent(view: android.view.View): Boolean {
   362→        // Only apply background to certain types of parent views to avoid breaking other UI elements
   363→        val className = view.javaClass.simpleName
   364→        return className.contains("FrameLayout") || 
   365→               className.contains("LinearLayout") ||
   366→               className.contains("PreferenceRecyclerViewFragment") ||
   367→               view.id == android.R.id.list_container ||
   368→               view.id == androidx.preference.R.id.recycler_view
   369→    }
   370→    
   371→    private fun forceFragmentBackgroundRefresh(fragmentView: android.view.View) {
   372→        try {
   373→            val typedValue = android.util.TypedValue()
   374→            val theme = requireContext().theme
   375→            
   376→            // Apply new theme background color to the fragment's root view
   377→            val backgroundAttrs = arrayOf(
   378→                com.omiyawaki.osrswiki.R.attr.paper_color,
   379→                com.google.android.material.R.attr.colorSurface,
   380→                android.R.attr.colorBackground
   381→            )
   382→            
