package com.omiyawaki.osrswiki.settings

import android.content.BroadcastReceiver
import android.content.Context
import android.content.Intent
import android.content.IntentFilter
import android.os.Bundle
import androidx.localbroadcastmanager.content.LocalBroadcastManager
import com.omiyawaki.osrswiki.util.log.L
import com.omiyawaki.osrswiki.R
import com.omiyawaki.osrswiki.activity.BaseActivity

/**
 * Activity to host the AppearanceSettingsFragment for appearance-specific settings.
 * Shows only theme, color, and display options - no offline/storage settings.
 */
class AppearanceSettingsActivity : BaseActivity() {

    private var themeChangeReceiver: BroadcastReceiver? = null

    companion object {
        /**
         * Creates an Intent to start AppearanceSettingsActivity.
         * @param context The Context to use.
         * @return An Intent to start AppearanceSettingsActivity.
         */
        fun newIntent(context: Context): Intent {
            return Intent(context, AppearanceSettingsActivity::class.java)
        }
    }

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_appearance_settings)

        if (savedInstanceState == null) {
            supportFragmentManager
                .beginTransaction()
                .replace(
                    R.id.appearance_settings_container, 
                    AppearanceSettingsFragment.newInstance(), 
                    AppearanceSettingsFragment.TAG
                )
                .commit()
        }

        setupToolbar()
        setupThemeChangeReceiver()
    }

    private fun setupToolbar() {
        supportActionBar?.apply {
            title = getString(R.string.settings_category_appearance)
            setDisplayHomeAsUpEnabled(true)
            setDisplayShowHomeEnabled(true)
        }
    }

    override fun onSupportNavigateUp(): Boolean {
        onBackPressedDispatcher.onBackPressed()
        return true
    }

    private fun setupThemeChangeReceiver() {
        themeChangeReceiver = object : BroadcastReceiver() {
            override fun onReceive(context: Context?, intent: Intent?) {
                if (intent?.action == AppearanceSettingsFragment.ACTION_THEME_CHANGED) {
                    L.d("AppearanceSettingsActivity: Received theme change broadcast")
                    // Apply theme dynamically without recreation
                    applyThemeDynamically()
                }
            }
        }

        val filter = IntentFilter(AppearanceSettingsFragment.ACTION_THEME_CHANGED)
        LocalBroadcastManager.getInstance(this).registerReceiver(themeChangeReceiver!!, filter)
        L.d("AppearanceSettingsActivity: Theme change receiver registered")
    }

    private fun unregisterThemeChangeReceiver() {
        themeChangeReceiver?.let { receiver ->
            LocalBroadcastManager.getInstance(this).unregisterReceiver(receiver)
            themeChangeReceiver = null
            L.d("AppearanceSettingsActivity: Theme change receiver unregistered")
        }
    }

    override fun refreshThemeDependentElements() {
        super.refreshThemeDependentElements()
        L.d("AppearanceSettingsActivity: Refreshing theme-dependent elements")

        try {
            // CRITICAL: Refresh status bar theming IMMEDIATELY and with proper timing
            // Status bar theming must happen early to avoid visual glitches
            setupStatusBarTheming()

            // Force a complete window refresh to apply status bar changes immediately
            window.decorView.invalidate()

            // Refresh the toolbar/action bar theme
            supportActionBar?.let { actionBar ->
                // Force toolbar colors to refresh
                val typedValue = android.util.TypedValue()
                val theme = this.theme

                // Apply toolbar background color
                if (theme.resolveAttribute(com.google.android.material.R.attr.colorSurface, typedValue, true)) {
                    // The toolbar should pick up the new theme automatically, but force refresh if needed
                    actionBar.setBackgroundDrawable(android.graphics.drawable.ColorDrawable(typedValue.data))
                }

                // Apply toolbar text color 
                if (theme.resolveAttribute(com.google.android.material.R.attr.colorOnSurface, typedValue, true)) {
                    // Unfortunately, ActionBar doesn't have a direct setTitleColor method
                    // The theme should handle this, but we can try to find and refresh the title TextView
                    try {
                        val titleId = resources.getIdentifier("action_bar_title", "id", "android")
                        if (titleId > 0) {
                            findViewById<android.widget.TextView>(titleId)?.setTextColor(typedValue.data)
                        }
                    } catch (e: Exception) {
                        L.d("AppearanceSettingsActivity: Could not manually set action bar title color: ${e.message}")
                    }
                }
            }

            // Refresh any other UI elements specific to this activity
            val containerView = findViewById<android.view.View>(R.id.appearance_settings_container)
            containerView?.let { container ->
                refreshViewBackground(container)
            }

            // CRITICAL: Force a second status bar refresh after other elements
            // This ensures status bar theming overrides any other conflicting settings
            window.decorView.post {
                setupStatusBarTheming()
            }

            L.d("AppearanceSettingsActivity: Theme-dependent elements refresh completed")

        } catch (e: Exception) {
            L.e("AppearanceSettingsActivity: Error refreshing theme elements: ${e.message}")
        }
    }

    private fun setupStatusBarTheming() {
        try {
            // Get the current theme's windowLightStatusBar setting
            val typedValue = android.util.TypedValue()
            val theme = this.theme
            val hasLightStatusBar = theme.resolveAttribute(android.R.attr.windowLightStatusBar, typedValue, true) && typedValue.data != 0

            // Apply the theme's status bar settings
            val windowInsetsController = androidx.core.view.ViewCompat.getWindowInsetsController(window.decorView)
            windowInsetsController?.let { controller ->
                controller.isAppearanceLightStatusBars = hasLightStatusBar
                L.d("AppearanceSettingsActivity: Set status bar light mode: $hasLightStatusBar")
            }

            // Set status bar color from theme if available
            val statusBarColorTypedValue = android.util.TypedValue()
            if (theme.resolveAttribute(android.R.attr.statusBarColor, statusBarColorTypedValue, true)) {
                window.statusBarColor = statusBarColorTypedValue.data
                L.d("AppearanceSettingsActivity: Applied theme status bar color")
            }
        } catch (e: Exception) {
            L.e("AppearanceSettingsActivity: Error setting up status bar theming: ${e.message}")
        }
    }

    private fun refreshViewBackground(view: android.view.View) {
        try {
            val typedValue = android.util.TypedValue()
            val theme = this.theme

            // Try to apply background color from theme
            val backgroundAttrs = arrayOf(
                R.attr.paper_color,
                com.google.android.material.R.attr.colorSurface,
                android.R.attr.colorBackground
            )

            for (attr in backgroundAttrs) {
                if (theme.resolveAttribute(attr, typedValue, true)) {
                    view.setBackgroundColor(typedValue.data)
                    L.d("AppearanceSettingsActivity: Applied background color from theme")
                    break
                }
            }
        } catch (e: Exception) {
            L.w("AppearanceSettingsActivity: Error refreshing view background: ${e.message}")
        }
    }

    override fun onDestroy() {
        unregisterThemeChangeReceiver()
        super.onDestroy()
    }
}

package com.omiyawaki.osrswiki.settings

import android.content.Intent
import android.os.Bundle
import android.view.View
import android.widget.TextView
import androidx.localbroadcastmanager.content.LocalBroadcastManager
import androidx.preference.ListPreference
import androidx.preference.Preference
import androidx.preference.PreferenceFragmentCompat
import androidx.recyclerview.widget.RecyclerView
import com.omiyawaki.osrswiki.OSRSWikiApp
import com.omiyawaki.osrswiki.R
import com.omiyawaki.osrswiki.theme.ThemeAware
import com.omiyawaki.osrswiki.util.applyAlegreyaHeadline
import com.omiyawaki.osrswiki.util.log.L

class AppearanceSettingsFragment : PreferenceFragmentCompat(), ThemeAware {

    override fun onCreatePreferences(savedInstanceState: Bundle?, rootKey: String?) {
        setPreferencesFromResource(R.xml.preferences_appearance, rootKey)

        val appThemeModePref = findPreference<ListPreference>(Prefs.KEY_APP_THEME_MODE)

        // Theme change listener for app theme mode preference.
        val themeChangeListener = Preference.OnPreferenceChangeListener { _, newValue ->
            L.d("AppearanceSettingsFragment: Theme change requested to: $newValue")

            // Post the theme change to ensure preference is persisted first
            view?.post {
                // Send broadcast to notify all activities about theme change
                // This will trigger broadcast receivers in all activities (including current one)
                // which will call BaseActivity.applyThemeDynamically() and trigger onThemeChanged() on ThemeAware fragments
                notifyGlobalThemeChange()
            }

            // Return true to allow the preference change to be saved.
            true
        }

        // Assign the listener to the app theme mode preference.
        appThemeModePref?.onPreferenceChangeListener = themeChangeListener
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        setupFonts()
    }

    private fun setupFonts() {
        L.d("AppearanceSettingsFragment: Setting up fonts...")

        // Apply fonts to preference TextViews by traversing the RecyclerView
        val recyclerView = listView as? RecyclerView
        recyclerView?.post {
            applyFontsToPreferences(recyclerView)
        }
    }

    private fun applyFontsToPreferences(recyclerView: RecyclerView) {
        for (i in 0 until recyclerView.childCount) {
            val itemView = recyclerView.getChildAt(i)
            applyFontsToPreferenceItem(itemView)
        }

        // Also set up a scroll listener to apply fonts to newly visible items
        recyclerView.addOnScrollListener(object : RecyclerView.OnScrollListener() {
            override fun onScrolled(recyclerView: RecyclerView, dx: Int, dy: Int) {
                super.onScrolled(recyclerView, dx, dy)
                for (i in 0 until recyclerView.childCount) {
                    val itemView = recyclerView.getChildAt(i)
                    applyFontsToPreferenceItem(itemView)
                }
            }
        })

        L.d("AppearanceSettingsFragment: Fonts applied to preference items")
    }

    private fun applyFontsToPreferenceItem(itemView: View) {
        // Let preference text use system fonts - no custom font application needed
        // This aligns with the app's migration to system fonts for non-heading text
    }

    override fun onThemeChanged() {
        if (!isAdded || view == null) {
            return
        }

        L.d("AppearanceSettingsFragment: onThemeChanged called - refreshing preferences UI")

        // Use the established ThemeAware callback to refresh the fragment UI
        refreshPreferencesThemeDirectly()
    }

    private fun refreshPreferencesThemeDirectly() {
        try {
            L.d("AppearanceSettingsFragment: Refreshing preferences theme directly")

            // Refresh the fragment's root view background
            view?.let { fragmentView ->
                val typedValue = android.util.TypedValue()
                val theme = requireContext().theme

                // Apply new background color
                val backgroundAttrs = arrayOf(
                    com.omiyawaki.osrswiki.R.attr.paper_color,
                    com.google.android.material.R.attr.colorSurface,
                    android.R.attr.colorBackground
                )

                for (attr in backgroundAttrs) {
                    if (theme.resolveAttribute(attr, typedValue, true)) {
                        fragmentView.setBackgroundColor(typedValue.data)
                        L.d("AppearanceSettingsFragment: Applied fragment background color")
                        break
                    }
                }
            }

            // Refresh the RecyclerView and preference items using proper preference refresh
            val recyclerView = listView as? RecyclerView
            recyclerView?.let { rv ->
                // Apply new theme background to the RecyclerView
                val typedValue = android.util.TypedValue()
                val theme = requireContext().theme

                if (theme.resolveAttribute(com.omiyawaki.osrswiki.R.attr.paper_color, typedValue, true)) {
                    rv.setBackgroundColor(typedValue.data)
                }

                // Force adapter refresh to pick up new theme colors - this is critical for preferences
                rv.adapter?.notifyDataSetChanged()

                // Force complete refresh
                rv.invalidate()
                rv.requestLayout()

                L.d("AppearanceSettingsFragment: RecyclerView refreshed with new theme")

                // CRITICAL: Apply theme colors to preference text views
                // PreferenceFragmentCompat uses complex view hierarchies that generic theme refresh misses
                refreshPreferenceTextViews(rv, theme)
            }

            L.d("AppearanceSettingsFragment: Preferences theme refresh completed successfully")

        } catch (e: Exception) {
            L.e("AppearanceSettingsFragment: Error refreshing preferences theme directly: ${e.message}")
        }
    }

    private fun refreshPreferenceTextViews(recyclerView: RecyclerView, theme: android.content.res.Resources.Theme) {
        try {
            L.d("AppearanceSettingsFragment: Refreshing preference text views with new theme")

            val typedValue = android.util.TypedValue()

            // Resolve theme colors for preferences
            val primaryTextColor = if (theme.resolveAttribute(com.google.android.material.R.attr.colorOnSurface, typedValue, true)) {
                typedValue.data
            } else if (theme.resolveAttribute(android.R.attr.textColorPrimary, typedValue, true)) {
                typedValue.data
            } else {
                null
            }

            val secondaryTextColor = if (theme.resolveAttribute(android.R.attr.textColorSecondary, typedValue, true)) {
                typedValue.data
            } else if (theme.resolveAttribute(com.google.android.material.R.attr.colorOnSurfaceVariant, typedValue, true)) {
                typedValue.data
            } else {
                null
            }

            // Traverse all visible preference items
            for (i in 0 until recyclerView.childCount) {
                val itemView = recyclerView.getChildAt(i)
                refreshPreferenceItemTextViews(itemView, primaryTextColor, secondaryTextColor)
            }

            // Also set up a scroll listener to refresh newly visible items
            // Remove existing listeners to avoid duplicates
            val existingListeners = mutableListOf<RecyclerView.OnScrollListener>()
            try {
                // Unfortunately there's no clean way to remove specific listeners, so we'll add ours
                recyclerView.addOnScrollListener(object : RecyclerView.OnScrollListener() {
                    override fun onScrolled(recyclerView: RecyclerView, dx: Int, dy: Int) {
                        super.onScrolled(recyclerView, dx, dy)
                        // Refresh any newly visible preference items
                        for (j in 0 until recyclerView.childCount) {
                            val itemView = recyclerView.getChildAt(j)
                            refreshPreferenceItemTextViews(itemView, primaryTextColor, secondaryTextColor)
                        }
                    }
                })
            } catch (e: Exception) {
                L.w("AppearanceSettingsFragment: Could not set scroll listener: ${e.message}")
            }

