/**
 * Loads real Varrock article HTML using PageRepository infrastructure.
 * Uses authentic wiki content with proper collapse table settings.
 */
private suspend fun loadVarrockArticleHtml(
    context: Context,
    collapseTablesEnabled: Boolean
): String = withContext(Dispatchers.IO) {
    try {
        Log.d(TAG, "Loading Varrock article with collapseTablesEnabled=$collapseTablesEnabled")

        // Get PageRepository from app instance (same as PageFragment)
        val app = context.applicationContext as OSRSWikiApp
        val pageRepository = app.pageRepository

        // Temporarily override collapse setting for this render
        val originalSetting = Prefs.isCollapseTablesEnabled
        try {
            // Temporarily set the collapse preference for this rendering
            // Note: This is a bit of a hack, but matches how the main app handles table collapse
            val prefsEditor = android.preference.PreferenceManager.getDefaultSharedPreferences(context).edit()
            prefsEditor.putBoolean(Prefs.KEY_COLLAPSE_TABLES, collapseTablesEnabled)
            prefsEditor.apply()

            // Fetch real Varrock article using PageRepository
            val pageResult = pageRepository.getArticleByTitle("Varrock", Theme.OSRS_LIGHT, false).first()

            when (pageResult) {
                is Result.Success -> {
                    val htmlContent = pageResult.data.htmlContent
                        ?: throw Exception("Article loaded but HTML content is null")
                    Log.d(TAG, "Successfully loaded Varrock article HTML (${htmlContent.length} characters)")
                    return@withContext htmlContent
                }
                is Result.Error -> {
                    val errorMessage = "Failed to load Varrock article: ${pageResult.message}"
                    Log.e(TAG, errorMessage, pageResult.throwable)
                    throw pageResult.throwable ?: Exception(pageResult.message)
                }
                is Result.Loading -> {
                    // This shouldn't happen with .first(), but handle gracefully
                    Log.w(TAG, "Still loading after .first() - falling back to error")
                    throw Exception("Article still loading after .first()")
                }
            }
        } finally {
            // Restore original setting
            val prefsEditor = android.preference.PreferenceManager.getDefaultSharedPreferences(context).edit()
            prefsEditor.putBoolean(Prefs.KEY_COLLAPSE_TABLES, originalSetting)
            prefsEditor.apply()
        }
    } catch (e: Exception) {
        Log.e(TAG, "Failed to load Varrock article HTML", e)
        throw e
    }
}

