package com.omiyawaki.osrswiki.settings

import android.content.Context
import android.graphics.Bitmap
import android.graphics.BitmapFactory
import android.graphics.Canvas
import android.graphics.Color
import android.graphics.Paint
import android.graphics.Rect
import android.util.DisplayMetrics
import android.util.Log
import android.util.LruCache
import android.view.ContextThemeWrapper
import android.view.LayoutInflater
import android.view.View
import android.webkit.WebView
import android.webkit.WebViewClient
import android.webkit.WebResourceRequest
import android.webkit.WebResourceResponse
import androidx.webkit.WebViewAssetLoader
import android.widget.FrameLayout
import androidx.appcompat.app.AppCompatActivity
import androidx.core.content.ContextCompat
import com.omiyawaki.osrswiki.views.ObservableWebView
import android.util.TypedValue
import android.content.res.Resources
import androidx.annotation.StyleRes
import com.omiyawaki.osrswiki.BuildConfig
import com.omiyawaki.osrswiki.R
import com.omiyawaki.osrswiki.OSRSWikiApp
import com.omiyawaki.osrswiki.page.PageHtmlBuilder
import com.omiyawaki.osrswiki.page.PageRepository
import com.omiyawaki.osrswiki.page.PageUiState
import com.omiyawaki.osrswiki.settings.Prefs
import com.omiyawaki.osrswiki.theme.Theme
import com.omiyawaki.osrswiki.util.Result
import kotlinx.coroutines.flow.first
import kotlinx.coroutines.flow.collect
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.withContext
import kotlinx.coroutines.withTimeout
import kotlinx.coroutines.suspendCancellableCoroutine
import kotlin.coroutines.resume
import kotlin.math.roundToInt
import java.io.File

/**
 * Generates table collapse preview by rendering the Varrock article with tables in specific state.
 * Each card shows a single state (collapsed or expanded) rather than A/B comparison.
 */
object TablePreviewRenderer {

    private const val TARGET_W_DP = 160 // Width for individual preview
    private const val TARGET_H_DP = 120 // Height for individual preview
    private const val TAG = "TablePreviewRenderer"

    // Memory cache for table previews
    private val memoryCache = object : LruCache<String, Bitmap>(getMaxCacheSize()) {
        override fun sizeOf(key: String, bitmap: Bitmap): Int {
            return bitmap.byteCount
        }
    }

    private fun getMaxCacheSize(): Int {
        val maxMemory = (Runtime.getRuntime().maxMemory() / 1024).toInt() // KB
        return maxMemory / 16 // Use smaller cache than theme previews (only 2 states vs 3)
    }

    /**
     * Gets a table preview bitmap showing the specific table collapse state.
     * @param context Application context
     * @param collapseTablesEnabled Whether tables should be collapsed in this preview
     */
    suspend fun getPreview(
        context: Context,
        collapseTablesEnabled: Boolean
    ): Bitmap = withContext(Dispatchers.Default) {
        val cacheKey = "table-preview-${if (collapseTablesEnabled) "collapsed" else "expanded"}-v${BuildConfig.VERSION_CODE}"

        Log.d(TAG, "getPreview called for collapseTablesEnabled=$collapseTablesEnabled")

        try {
            // Check memory cache first
            memoryCache.get(cacheKey)?.let { cached ->
                Log.d(TAG, "Found cached table preview ($cacheKey) - ${cached.width}×${cached.height}")
                return@withContext cached
            }

            // Check disk cache
            val cacheDir = File(context.cacheDir, "table_previews").apply { mkdirs() }
            val cachedFile = File(cacheDir, "$cacheKey.webp")

            if (cachedFile.exists()) {
                Log.d(TAG, "Found disk cached table preview ($cacheKey)")
                val bitmap = BitmapFactory.decodeFile(cachedFile.absolutePath)
                if (bitmap != null && !bitmap.isRecycled) {
                    Log.d(TAG, "Loaded disk cached table preview - ${bitmap.width}×${bitmap.height}")
                    memoryCache.put(cacheKey, bitmap)
                    return@withContext bitmap
                } else {
                    Log.w(TAG, "Disk cached bitmap was null or recycled, deleting cache file")
                    cachedFile.delete()
                }
            }

            // Generate new preview
            Log.d(TAG, "Generating new table preview for collapseTablesEnabled=$collapseTablesEnabled")
            val newBitmap = generateSingleTablePreview(context, collapseTablesEnabled)

            if (newBitmap.isRecycled) {
                Log.e(TAG, "Generated bitmap is recycled! This is a bug.")
                return@withContext generateFallbackBitmap(context, collapseTablesEnabled)
            }

            // Cache in memory
            memoryCache.put(cacheKey, newBitmap)
            Log.d(TAG, "Cached new table preview in memory")

            // Save to disk cache
            try {
                cachedFile.outputStream().use { stream ->
                    val compressed = newBitmap.compress(Bitmap.CompressFormat.WEBP_LOSSLESS, 100, stream)
                    if (compressed) {
                        Log.d(TAG, "Saved table preview to disk cache")
                    } else {
                        Log.w(TAG, "Failed to compress table preview for disk cache")
                    }
                }
            } catch (e: Exception) {
                Log.w(TAG, "Disk caching failed for table preview: ${e.message}")
            }

            Log.d(TAG, "Successfully generated ${newBitmap.width}×${newBitmap.height} table preview")
            newBitmap
        } catch (e: Exception) {
            Log.e(TAG, "getPreview FAILED", e)
            e.printStackTrace()
            generateFallbackBitmap(context, collapseTablesEnabled)
        }
    }


    /**
     * Generates a single table preview by loading real Varrock article and applying collapse setting.
     */
    private suspend fun generateSingleTablePreview(
        context: Context,
        collapseTablesEnabled: Boolean
    ): Bitmap = withContext(Dispatchers.Main) {
        try {
            Log.d(TAG, "Starting generateSingleTablePreview for collapseTablesEnabled=$collapseTablesEnabled")

            // Get real Varrock article HTML with proper collapse setting
            val articleHtml = loadVarrockArticleHtml(context, collapseTablesEnabled)

            // Render using WebView with proper callback
            val bitmap = renderHtmlWithWebView(context, articleHtml, collapseTablesEnabled)

            Log.d(TAG, "Single table preview generated - ${bitmap.width}×${bitmap.height}")
            bitmap

        } catch (e: Exception) {
            Log.e(TAG, "generateSingleTablePreview FAILED for collapseTablesEnabled=$collapseTablesEnabled", e)
            e.printStackTrace()
            generateFallbackBitmap(context, collapseTablesEnabled)
        }
    }

    /**
     * Loads real Varrock article HTML using PageRepository infrastructure.
     * Uses authentic wiki content - collapse state will be controlled via JavaScript after load.
     */
    private suspend fun loadVarrockArticleHtml(
        context: Context,
        collapseTablesEnabled: Boolean
    ): String = withContext(Dispatchers.IO) {
        try {
            Log.d(TAG, "Loading Varrock article - collapse state will be set via JavaScript (target state: $collapseTablesEnabled)")

            // Get PageRepository from app instance (same as PageFragment)
            val app = context.applicationContext as OSRSWikiApp
            val pageRepository = app.pageRepository

            // Fetch real Varrock article using PageRepository (no preference manipulation needed)
            // Use timeout and collect to handle loading states properly
            var pageResult: Result<PageUiState>? = null
            val timeoutMillis = 10000L // 10 second timeout

            try {
                withTimeout(timeoutMillis) {
                    pageRepository.getArticleByTitle("Varrock", Theme.OSRS_LIGHT, false).collect { result ->
                        when (result) {
                            is Result.Loading -> {
                                Log.d(TAG, "PageRepository still loading Varrock article...")
                                // Continue waiting
                            }
                            is Result.Success, is Result.Error -> {
                                Log.d(TAG, "PageRepository completed with result: ${result::class.simpleName}")
                                pageResult = result
                                return@collect // Stop collecting after first non-loading result
                            }
                        }
                    }
                }
            } catch (e: kotlinx.coroutines.TimeoutCancellationException) {
                Log.e(TAG, "Timeout waiting for Varrock article to load")
                throw Exception("Timeout loading Varrock article after ${timeoutMillis}ms")
            }

            when (val result = pageResult) {
                is Result.Success -> {
                    val htmlContent = result.data.htmlContent
                        ?: throw Exception("Article loaded but HTML content is null")
                    Log.d(TAG, "Successfully loaded Varrock article HTML (${htmlContent.length} characters)")
                    return@withContext htmlContent
                }
                is Result.Error -> {
                    val errorMessage = "Failed to load Varrock article: ${result.message}"
                    Log.e(TAG, errorMessage, result.throwable)
                    throw result.throwable ?: Exception(result.message)
                }
                null -> {
                    Log.e(TAG, "PageRepository never completed - this should not happen")
                    throw Exception("PageRepository never completed loading")
                }
            }
        } catch (e: Exception) {
            Log.e(TAG, "Failed to load Varrock article HTML", e)
            throw e
        }
    }

    /**
     * Applies table collapse state to WebView via JavaScript after page load.
     * This controls whether tables appear collapsed or expanded in the preview.
     */
    private fun applyTableCollapseState(webView: WebView, collapseTablesEnabled: Boolean, onFinished: () -> Unit) {
        val script = if (collapseTablesEnabled) {
            // Ensure all tables are collapsed
            """
            (function() {
                console.log('TablePreview: Ensuring all tables are collapsed');
                const containers = document.querySelectorAll('.collapsible-container');
                containers.forEach(function(container) {
                    if (!container.classList.contains('collapsed')) {
                        container.classList.add('collapsed');
                        const content = container.querySelector('.collapsible-content');
                        if (content) {
                            content.style.height = '0px';
                        }
                    }
                });
                console.log('TablePreview: Applied collapsed state to ' + containers.length + ' containers');
            })();
            """.trimIndent()
        } else {
            // Ensure all tables are expanded
            """
            (function() {
                console.log('TablePreview: Ensuring all tables are expanded');
                const containers = document.querySelectorAll('.collapsible-container');
                containers.forEach(function(container) {
                    if (container.classList.contains('collapsed')) {
                        container.classList.remove('collapsed');
                        const content = container.querySelector('.collapsible-content');
                        if (content) {
                            content.style.height = 'auto';
                        }
                    }
                });
                console.log('TablePreview: Applied expanded state to ' + containers.length + ' containers');
            })();
            """.trimIndent()
        }

        Log.d(TAG, "Applying table collapse state: collapseTablesEnabled=$collapseTablesEnabled")
        webView.evaluateJavascript(script) { result ->
            Log.d(TAG, "Table collapse state applied, result: $result")
            onFinished()
        }
    }

    /**
     * Applies theme colors to WebView matching PageWebViewManager.applyThemeColors
     */
    private fun applyThemeColorsToWebView(webView: WebView, context: Context, onFinished: () -> Unit) {
        val themeColors = mapOf(
            "--colorsurface" to getThemeColor(context, com.google.android.material.R.attr.colorSurface, "#FFFFFF"),
            "--coloronsurface" to getThemeColor(context, com.google.android.material.R.attr.colorOnSurface, "#000000"),
            "--colorsurfacevariant" to getThemeColor(context, com.google.android.material.R.attr.colorSurfaceVariant, "#E0E0E0"),
            "--coloronsurfacevariant" to getThemeColor(context, com.google.android.material.R.attr.colorOnSurfaceVariant, "#424242"),
            "--colorprimarycontainer" to getThemeColor(context, com.google.android.material.R.attr.colorPrimaryContainer, "#D0C0A0"),
            "--coloronprimarycontainer" to getThemeColor(context, com.google.android.material.R.attr.colorOnPrimaryContainer, "#000000")
        )

        val jsObjectString = themeColors.entries.joinToString(separator = ",\\n") {
            "    '${it.key}': '${it.value}'"
        }

        val script = """
        (function() {
            const themeColors = {
            $jsObjectString
            };
            for (const [key, value] of Object.entries(themeColors)) {
                document.documentElement.style.setProperty(key, value);
            }
        })();
        """.trimIndent()

        Log.d(TAG, "Applying theme colors via JavaScript")
        webView.evaluateJavascript(script) {
            Log.d(TAG, "Theme colors applied, proceeding with rendering")
            onFinished()
        }
    }

    /**
     * Gets theme color matching PageWebViewManager.getThemeColor
     */
    private fun getThemeColor(context: Context, attrId: Int, fallback: String): String {
        val typedValue = TypedValue()
        if (!context.theme.resolveAttribute(attrId, typedValue, true)) {
            Log.e(TAG, "Failed to resolve theme attribute ID #$attrId")
            return fallback
        }
        val color: Int = try {
            if (typedValue.type >= TypedValue.TYPE_FIRST_COLOR_INT && typedValue.type <= TypedValue.TYPE_LAST_COLOR_INT) {
                typedValue.data
            } else {
                ContextCompat.getColor(context, typedValue.resourceId)
            }
        } catch (e: Resources.NotFoundException) {
            Log.e(TAG, "Color resource not found for attribute ID #$attrId", e)
            return fallback
        }
        return String.format("#%06X", (0xFFFFFF and color))
    }

    /**
     * Renders HTML content using WebView with proper asset loading, matching main app configuration.
     * Uses Activity-attached WebView for proper rendering.
     */
    private suspend fun renderHtmlWithWebView(
        context: Context,
        htmlContent: String,
        collapseTablesEnabled: Boolean
    ): Bitmap = withContext(Dispatchers.Main) {
        suspendCancellableCoroutine { continuation ->
            try {
                // Get Activity context - critical for WebView rendering
                val activity = when (context) {
                    is AppCompatActivity -> context
                    is ContextThemeWrapper -> {
                        var baseContext = context.baseContext
                        while (baseContext is ContextThemeWrapper) {
                            baseContext = baseContext.baseContext
                        }
                        baseContext as? AppCompatActivity
                    }
                    else -> null
                } ?: run {
                    Log.e(TAG, "Cannot find Activity context for WebView rendering")
                    continuation.resume(generateFallbackBitmap(context, collapseTablesEnabled))
                    return@suspendCancellableCoroutine
                }

                Log.d(TAG, "Creating Activity-attached WebView for rendering")

                // Create themed context with Activity
                val themedContext = ContextThemeWrapper(activity, R.style.Theme_OSRSWiki_OSRSLight)

                // Create ObservableWebView like main app
                val webView = ObservableWebView(themedContext)

                // Get dimensions - render at full device screen size, then scale down
                val dm = context.resources.displayMetrics
                val fullScreenW = dm.widthPixels
                val fullScreenH = dm.heightPixels
                val targetPreviewW = (TARGET_W_DP * dm.density).roundToInt()
                val targetPreviewH = (TARGET_H_DP * dm.density).roundToInt()

                Log.d(TAG, "Full-screen WebView dimensions: ${fullScreenW}×${fullScreenH}")
                Log.d(TAG, "Target preview dimensions: ${targetPreviewW}×${targetPreviewH}")

                // Create container at full screen size and attach to Activity's view hierarchy (invisible)
                val container = FrameLayout(themedContext).apply {
                    layoutParams = FrameLayout.LayoutParams(fullScreenW, fullScreenH)
                    visibility = View.INVISIBLE // Hidden but attached
                    addView(webView, FrameLayout.LayoutParams(fullScreenW, fullScreenH))
                }

                // Attach to Activity's content view temporarily
                val rootView = activity.findViewById<FrameLayout>(android.R.id.content)
                rootView.addView(container)

                // Set up WebViewAssetLoader exactly like the main app
                val localAssetDomain = "appassets.androidplatform.net"
                val assetLoader = WebViewAssetLoader.Builder()
                    .setDomain(localAssetDomain)
                    .addPathHandler("/assets/", WebViewAssetLoader.AssetsPathHandler(themedContext))
                    .addPathHandler("/res/", WebViewAssetLoader.ResourcesPathHandler(themedContext))
                    .build()

                // Configure WebView settings exactly like PageWebViewManager
                webView.settings.apply {
                    javaScriptEnabled = true
                    domStorageEnabled = true
                    allowFileAccess = false
                    allowContentAccess = false
                    mixedContentMode = android.webkit.WebSettings.MIXED_CONTENT_NEVER_ALLOW
                }

                // Set up WebView client with asset loading matching PageWebViewManager
                webView.webViewClient = object : WebViewClient() {
                    override fun shouldInterceptRequest(
                        view: WebView,
                        request: WebResourceRequest
                    ): WebResourceResponse? {
                        val urlString = request.url.toString()
                        val localAssetResponse = assetLoader.shouldInterceptRequest(request.url)
                        if (localAssetResponse != null) {
                            Log.i(TAG, "Asset loaded successfully: $urlString")
                            return localAssetResponse
                        }
                        Log.w(TAG, "Asset not found: $urlString")
                        return super.shouldInterceptRequest(view, request)
                    }

                    override fun onPageFinished(view: WebView, url: String) {
                        try {
                            Log.d(TAG, "WebView page finished loading")

                            // Check if content is actually loaded
                            view.evaluateJavascript("document.body ? document.body.innerHTML.length : 0") { result ->
                                Log.d(TAG, "WebView content length: $result characters")
                            }

                            // Apply theme colors like main app does
                            applyThemeColorsToWebView(view, themedContext) {
                                // Apply table collapse state via JavaScript
                                applyTableCollapseState(view, collapseTablesEnabled) {
                                    // Make body visible (matching main app finalizeAndRevealPage)
                                    view.evaluateJavascript("document.body.style.visibility = 'visible';") { _ ->
                                        // Wait longer for CSS rendering and theme application
                                        view.postDelayed({
                                        try {
                                            Log.d(TAG, "Capturing full-screen WebView bitmap after theme and visibility")

                                            // Force layout update at full screen size
                                            container.measure(
                                                View.MeasureSpec.makeMeasureSpec(fullScreenW, View.MeasureSpec.EXACTLY),
                                                View.MeasureSpec.makeMeasureSpec(fullScreenH, View.MeasureSpec.EXACTLY)
                                            )
                                            container.layout(0, 0, fullScreenW, fullScreenH)

                                            // Create full-screen bitmap and draw
                                            val fullBitmap = Bitmap.createBitmap(fullScreenW, fullScreenH, Bitmap.Config.ARGB_8888)
                                            val canvas = Canvas(fullBitmap)
                                            canvas.drawColor(android.graphics.Color.WHITE) // Ensure background
                                            container.draw(canvas)

                                            Log.d(TAG, "Full-screen bitmap created - ${fullBitmap.width}×${fullBitmap.height}")

                                            // Scale down preserving aspect ratio and crop to fit (like theme previews)
                                            val scaledBitmap = createAspectRatioPreservingBitmap(
                                                fullBitmap, 
                                                targetPreviewW, 
                                                targetPreviewH
                                            )

                                            scaledBitmap.density = DisplayMetrics.DENSITY_DEFAULT

                                            Log.d(TAG, "Scaled preview bitmap created - ${scaledBitmap.width}×${scaledBitmap.height}")

                                            // Clean up - remove from Activity and recycle full bitmap
                                            rootView.removeView(container)
                                            webView.destroy()
                                            fullBitmap.recycle() // Free memory

                                            continuation.resume(scaledBitmap)

                                        } catch (e: Exception) {
                                            Log.e(TAG, "Error capturing WebView bitmap", e)
                                            rootView.removeView(container)
                                            webView.destroy()
                                            continuation.resume(generateFallbackBitmap(context, collapseTablesEnabled))
                                        }
                                    }, 300) // Longer delay for theme application
                                    }
                                }
                            }

                        } catch (e: Exception) {
                            Log.e(TAG, "Error in onPageFinished", e)
                            rootView.removeView(container)
                            webView.destroy()
                            continuation.resume(generateFallbackBitmap(context, collapseTablesEnabled))
                        }
                    }

                    override fun onReceivedError(view: WebView, errorCode: Int, description: String, failingUrl: String) {
                        Log.e(TAG, "WebView error: $description for URL: $failingUrl")
                        rootView.removeView(container)
                        webView.destroy()
                        continuation.resume(generateFallbackBitmap(context, collapseTablesEnabled))
                    }
                }

                // Load HTML with correct base URL (same as main app)
                val baseUrl = "https://$localAssetDomain/"
                Log.d(TAG, "Loading HTML with base URL: $baseUrl")
                webView.loadDataWithBaseURL(
                    baseUrl,
                    htmlContent,
                    "text/html",
                    "UTF-8",
                    null
                )

                // Set up cancellation
                continuation.invokeOnCancellation {
                    Log.d(TAG, "WebView rendering cancelled")
                    try {
                        rootView.removeView(container)
                        webView.destroy()
                    } catch (e: Exception) {
                        Log.w(TAG, "Error during cancellation cleanup: ${e.message}")
                    }
                }

            } catch (e: Exception) {
                Log.e(TAG, "Error setting up WebView", e)
                continuation.resume(generateFallbackBitmap(context, collapseTablesEnabled))
            }
        }
    }

    /**
     * Creates a preview bitmap that preserves aspect ratio and crops to fit target dimensions.
     * Similar to how image thumbnails work - no distortion, shows as much content as possible.
     */
    private fun createAspectRatioPreservingBitmap(
        sourceBitmap: Bitmap,
        targetWidth: Int,
        targetHeight: Int
    ): Bitmap {
        val sourceWidth = sourceBitmap.width.toFloat()
        val sourceHeight = sourceBitmap.height.toFloat()
        val targetRatio = targetWidth.toFloat() / targetHeight.toFloat()
        val sourceRatio = sourceWidth / sourceHeight

        // Calculate scaling factor to fill the target container
        val scale = if (sourceRatio > targetRatio) {
            // Source is wider - scale to fit height, crop width
            targetHeight.toFloat() / sourceHeight
        } else {
            // Source is taller - scale to fit width, crop height
            targetWidth.toFloat() / sourceWidth
        }

        // Calculate scaled dimensions
        val scaledWidth = (sourceWidth * scale).toInt()
        val scaledHeight = (sourceHeight * scale).toInt()

        // Create scaled bitmap
        val scaledBitmap = Bitmap.createScaledBitmap(
            sourceBitmap, 
            scaledWidth, 
            scaledHeight, 
            true
        )

        // Calculate crop offsets to center the content
        val cropX = maxOf(0, (scaledWidth - targetWidth) / 2)
        val cropY = 0 // Start from top to show header content

        // Crop to final target size
        val croppedBitmap = Bitmap.createBitmap(
            scaledBitmap,
            cropX,
            cropY,
            minOf(targetWidth, scaledWidth),
            minOf(targetHeight, scaledHeight),
            null,
            false
        )

        // Clean up intermediate bitmap
        if (scaledBitmap != croppedBitmap) {
            scaledBitmap.recycle()
        }

        Log.d(TAG, "Aspect ratio preserved: ${sourceWidth}×${sourceHeight} -> ${scaledWidth}×${scaledHeight} -> ${croppedBitmap.width}×${croppedBitmap.height}")

        return croppedBitmap
    }

    /**
     * Creates a fallback bitmap when table preview generation fails.
     */
    private fun generateFallbackBitmap(context: Context, collapseTablesEnabled: Boolean): Bitmap {
        val dm = context.resources.displayMetrics
        val width = (TARGET_W_DP * dm.density).roundToInt()
        val height = (TARGET_H_DP * dm.density).roundToInt()

        val bitmap = Bitmap.createBitmap(width, height, Bitmap.Config.ARGB_8888)
        val canvas = Canvas(bitmap)

        // Fill with neutral background
        canvas.drawColor(Color.parseColor("#F5F5F5"))

        // Draw simple text indicator
        val paint = Paint().apply {
            color = Color.parseColor("#666666")
            textSize = 16 * dm.density
            textAlign = Paint.Align.CENTER
        }

        val text = if (collapseTablesEnabled) "Collapsed" else "Expanded"
        canvas.drawText(
            text,
            (width / 2).toFloat(),
            (height / 2).toFloat(),
            paint
        )

        bitmap.density = DisplayMetrics.DENSITY_DEFAULT
        return bitmap
    }

    /**
     * Converts dp to pixels.
     */
    private fun dpToPx(dp: Int, context: Context): Int {
        return (dp * context.resources.displayMetrics.density).toInt()
    }

    /**
     * Gets the preview dimensions for UI sizing.
     * Returns Pair(widthDp, heightDp) for individual preview cards.
     */
    fun getPreviewDimensionsForUI(context: Context): Pair<Int, Int> {
        return Pair(TARGET_W_DP, TARGET_H_DP) // Individual card dimensions
    }

    /**
     * Clears all table preview caches.
     */
    fun clearCache(context: Context) {
        Log.d(TAG, "Clearing all table preview caches")
        memoryCache.evictAll()

        val cacheDir = File(context.cacheDir, "table_previews")
        if (cacheDir.exists()) {
            val files = cacheDir.listFiles()
            files?.forEach { file ->
                val deleted = file.delete()
                Log.d(TAG, "Deleted table preview cache file ${file.name}: $deleted")
            }
            Log.d(TAG, "Cleared ${files?.size ?: 0} table preview cache files")
        }
    }
}

