   465→                            val unselectedColor = typedValue.data
   466→                            
   467→                            // Create new color state list
   468→                            val states = arrayOf(
   469→                                intArrayOf(android.R.attr.state_checked),
   470→                                intArrayOf(-android.R.attr.state_checked)
   471→                            )
   472→                            val colors = intArrayOf(selectedColor, unselectedColor)
   473→                            val colorStateList = ColorStateList(states, colors)
   474→                            
