   521→                    android.R.attr.colorBackground
   522→                )
   523→                
   524→                for (backgroundAttr in backgroundColorAttrs) {
   525→                    if (theme.resolveAttribute(backgroundAttr, bgTypedValue, true)) {
   526→                        bottomNav.setBackgroundColor(bgTypedValue.data)
   527→                        android.util.Log.d("BaseActivity", "Updated nav background: ${Integer.toHexString(bgTypedValue.data)}")
   528→                        break
   529→                    }
   530→                }
   531→                
   532→            } catch (e: Exception) {
   533→                android.util.Log.w("BaseActivity", "Method 4 failed for BottomNavigationView refresh", e)
