   214→    private fun forceCachedUIElementRefresh(view: View) {
   215→        try {
   216→            // Force invalidate the view and all its children recursively
   217→            invalidateViewRecursively(view)
   218→            
   219→            // Handle specific view types that are known to cache styling
   220→            when (view) {
   221→                is androidx.recyclerview.widget.RecyclerView -> {
   222→                    // Force adapter refresh for RecyclerViews
   223→                    view.adapter?.notifyDataSetChanged()
   224→                    view.invalidateItemDecorations()
   225→                    android.util.Log.d("BaseActivity", "Forced RecyclerView refresh")
   226→                }
   227→                is androidx.viewpager2.widget.ViewPager2 -> {
   228→                    // Force ViewPager2 refresh
   229→                    view.adapter?.notifyDataSetChanged()
   230→                    android.util.Log.d("BaseActivity", "Forced ViewPager2 refresh")
   231→                }
   232→                is androidx.fragment.app.FragmentContainerView -> {
   233→                    // Handle fragment container views
   234→                    view.invalidate()
   235→                    view.requestLayout()
   236→                    android.util.Log.d("BaseActivity", "Forced FragmentContainerView refresh")
   237→                }
   238→            }
   239→            
   240→            // If this is a ViewGroup, also handle special ViewGroup types
   241→            if (view is ViewGroup) {
   242→                when (view.javaClass.simpleName) {
   243→                    "ConstraintLayout", "LinearLayout", "FrameLayout", "RelativeLayout" -> {
   244→                        // Force layout refresh for common layout types
   245→                        view.requestLayout()
   246→                    }
   247→                    "AppBarLayout", "CollapsingToolbarLayout" -> {
   248→                        // Force refresh for Material Design layout components
   249→                        view.invalidate()
   250→                        view.requestLayout()
   251→                        android.util.Log.d("BaseActivity", "Forced Material layout refresh: ${view.javaClass.simpleName}")
   252→                    }
   253→                }
   254→                
   255→                // Recursively apply to all children
   256→                for (i in 0 until view.childCount) {
   257→                    forceCachedUIElementRefresh(view.getChildAt(i))
   258→                }
   259→            }
   260→            
   261→        } catch (e: Exception) {
   262→            android.util.Log.w("BaseActivity", "Error in forceCachedUIElementRefresh for ${view.javaClass.simpleName}", e)
   263→        }
   264→    }
