   448→    private fun refreshBottomNavigationTheme(bottomNav: BottomNavigationView, theme: android.content.res.Resources.Theme) {
   449→        try {
   450→            android.util.Log.d("BaseActivity", "Refreshing BottomNavigationView theme comprehensively")
   451→            
   452→            // Method 1: Force active indicator style refresh (CRITICAL for pill background)
   453→            try {
   454→                val typedValue = TypedValue()
   455→                
   456→                // Get the new active indicator color from theme
   457→                if (theme.resolveAttribute(com.google.android.material.R.attr.colorSurfaceVariant, typedValue, true)) {
   458→                    val indicatorColor = typedValue.data
   459→                    android.util.Log.d("BaseActivity", "Setting new indicator color: ${Integer.toHexString(indicatorColor)}")
   460→                    
   461→                    // Try to programmatically update the active indicator color
   462→                    // This is tricky because Material components cache the style internally
   463→                    bottomNav.itemActiveIndicatorColor = ColorStateList.valueOf(indicatorColor)
   464→                }
   465→            } catch (e: Exception) {
   466→                android.util.Log.w("BaseActivity", "Failed to refresh active indicator color", e)
   467→            }
   468→            
   469→            // Method 2: Update icon and text color state lists with comprehensive attribute resolution
   470→            try {
   471→                val typedValue = TypedValue()
   472→                
   473→                // Get new colors from theme using comprehensive attribute list
   474→                if (theme.resolveAttribute(com.google.android.material.R.attr.colorSecondary, typedValue, true)) {
   475→                    val selectedColor = typedValue.data
   476→                    // Try multiple attributes for unselected color based on layout analysis
   477→                    val unselectedColorAttrs = arrayOf(
   478→                        com.google.android.material.R.attr.colorOnSurface,          // Used in bottom nav selectors
   479→                        com.google.android.material.R.attr.colorOnSurfaceVariant,   // Alternative for unselected state
   480→                        R.attr.primary_text_color,              // App fallback
   481→                        android.R.attr.textColorPrimary         // System fallback
   482→                    )
   483→                    
   484→                    for (unselectedAttr in unselectedColorAttrs) {
   485→                        if (theme.resolveAttribute(unselectedAttr, typedValue, true)) {
   486→                            val unselectedColor = typedValue.data
   487→                            
   488→                            // Create new color state list
   489→                            val states = arrayOf(
   490→                                intArrayOf(android.R.attr.state_checked),
   491→                                intArrayOf(-android.R.attr.state_checked)
   492→                            )
   493→                            val colors = intArrayOf(selectedColor, unselectedColor)
   494→                            val colorStateList = ColorStateList(states, colors)
   495→                            
   496→                            bottomNav.itemIconTintList = colorStateList
   497→                            bottomNav.itemTextColor = colorStateList
   498→                            android.util.Log.d("BaseActivity", "Updated nav colors (selected: ${Integer.toHexString(selectedColor)}, unselected: ${Integer.toHexString(unselectedColor)})")
   499→                            break // Use the first unselected color that resolves
   500→                        }
   501→                    }
   502→                }
   503→            } catch (e: Exception) {
   504→                android.util.Log.w("BaseActivity", "Method 2 failed for BottomNavigationView refresh", e)
   505→            }
   506→            
   507→            // Method 3: Force Material component internal refresh via selection cycling
   508→            try {
   509→                val currentSelection = bottomNav.selectedItemId
   510→                val menu = bottomNav.menu
   511→                
   512→                if (menu.size() > 1) {
   513→                    // Find a different item to temporarily select
   514→                    for (i in 0 until menu.size()) {
   515→                        val item = menu.getItem(i)
   516→                        if (item.itemId != currentSelection) {
   517→                            // Temporarily change selection to force internal style refresh
   518→                            bottomNav.selectedItemId = item.itemId
   519→                            bottomNav.post {
   520→                                // Restore original selection
   521→                                bottomNav.selectedItemId = currentSelection
   522→                                android.util.Log.d("BaseActivity", "Forced navigation refresh via selection cycling")
   523→                            }
   524→                            break
   525→                        }
   526→                    }
   527→                }
   528→            } catch (e: Exception) {
   529→                android.util.Log.w("BaseActivity", "Method 3 failed for BottomNavigationView refresh", e)
   530→            }
   531→            
   532→            // Method 4: Force complete view system refresh
   533→            try {
   534→                bottomNav.invalidate()
   535→                bottomNav.requestLayout()
   536→                
   537→                // Also try to refresh the background
   538→                val bgTypedValue = TypedValue()
   539→                val backgroundColorAttrs = arrayOf(
   540→                    com.google.android.material.R.attr.colorSurface,
   541→                    R.attr.paper_color,
   542→                    android.R.attr.colorBackground
   543→                )
   544→                
   545→                for (backgroundAttr in backgroundColorAttrs) {
   546→                    if (theme.resolveAttribute(backgroundAttr, bgTypedValue, true)) {
   547→                        bottomNav.setBackgroundColor(bgTypedValue.data)
   548→                        android.util.Log.d("BaseActivity", "Updated nav background: ${Integer.toHexString(bgTypedValue.data)}")
   549→                        break
   550→                    }
   551→                }
   552→                
   553→            } catch (e: Exception) {
   554→                android.util.Log.w("BaseActivity", "Method 4 failed for BottomNavigationView refresh", e)
   555→            }
   556→            
   557→        } catch (e: Exception) {
   558→            android.util.Log.e("BaseActivity", "Error refreshing BottomNavigationView theme", e)
   559→        }
   560→    }
