   688→        return background?.constantState == null || 
   689→               background.javaClass.simpleName.contains("ColorDrawable") ||
   690→               background.javaClass.simpleName.contains("GradientDrawable") ||
   691→               background.javaClass.simpleName.contains("RippleDrawable")
   692→    }
