    32→    override fun onResume() {
    33→        super.onResume()
    34→        // When resuming, check if the global theme has changed since this activity
    35→        // was last created. If so, apply the new theme dynamically without recreation.
    36→        val globalThemeId = (application as OSRSWikiApp).getCurrentTheme().resourceId
    37→        if (currentThemeId != 0 && currentThemeId != globalThemeId) {
    38→            android.util.Log.d("BaseActivity", "Theme changed during resume, applying dynamically")
