    63→    /**
    64→     * Notifies all ThemeAware fragments that the theme has changed.
    65→     * Should be called after the activity has been recreated due to a theme change.
    66→     */
    67→    protected fun notifyFragmentsOfThemeChange() {
    68→        supportFragmentManager.fragments.forEach { fragment ->
    69→            if (fragment is ThemeAware && fragment.isAdded && fragment.view != null) {
    70→                try {
    71→                    fragment.onThemeChanged()
    72→                } catch (e: Exception) {
    73→                    // Log the error but don't crash - theme changes should be graceful
    74→                    android.util.Log.e("BaseActivity", "Error notifying fragment ${fragment::class.simpleName} of theme change", e)
    75→                }
    76→            }
    77→        }
    78→    }
