   540→    private fun refreshBottomNavigationTheme(bottomNav: BottomNavigationView, theme: android.content.res.Resources.Theme) {
   541→        try {
   542→            android.util.Log.d("BaseActivity", "Refreshing BottomNavigationView theme comprehensively")
   543→            
   544→            // Method 1: Force active indicator style refresh (CRITICAL for pill background)
   545→            try {
   546→                val typedValue = TypedValue()
   547→                
   548→                // Get the new active indicator color from theme
   549→                if (theme.resolveAttribute(com.google.android.material.R.attr.colorSurfaceVariant, typedValue, true)) {
   550→                    val indicatorColor = typedValue.data
   551→                    android.util.Log.d("BaseActivity", "Setting new indicator color: ${Integer.toHexString(indicatorColor)}")
   552→                    
   553→                    // Try to programmatically update the active indicator color
   554→                    // This is tricky because Material components cache the style internally
   555→                    bottomNav.itemActiveIndicatorColor = ColorStateList.valueOf(indicatorColor)
   556→                }
   557→            } catch (e: Exception) {
   558→                android.util.Log.w("BaseActivity", "Failed to refresh active indicator color", e)
   559→            }
   560→            
   561→            // Method 2: Update icon and text color state lists with comprehensive attribute resolution
   562→            try {
   563→                val typedValue = TypedValue()
   564→                
   565→                // Get new colors from theme using comprehensive attribute list
   566→                if (theme.resolveAttribute(com.google.android.material.R.attr.colorSecondary, typedValue, true)) {
   567→                    val selectedColor = typedValue.data
   568→                    // Try multiple attributes for unselected color based on layout analysis
   569→                    val unselectedColorAttrs = arrayOf(
   570→                        com.google.android.material.R.attr.colorOnSurface,          // Used in bottom nav selectors
   571→                        com.google.android.material.R.attr.colorOnSurfaceVariant,   // Alternative for unselected state
   572→                        R.attr.primary_text_color,              // App fallback
   573→                        android.R.attr.textColorPrimary         // System fallback
   574→                    )
   575→                    
   576→                    for (unselectedAttr in unselectedColorAttrs) {
   577→                        if (theme.resolveAttribute(unselectedAttr, typedValue, true)) {
   578→                            val unselectedColor = typedValue.data
   579→                            
   580→                            // Create new color state list
   581→                            val states = arrayOf(
   582→                                intArrayOf(android.R.attr.state_checked),
   583→                                intArrayOf(-android.R.attr.state_checked)
   584→                            )
   585→                            val colors = intArrayOf(selectedColor, unselectedColor)
   586→                            val colorStateList = ColorStateList(states, colors)
   587→                            
   588→                            bottomNav.itemIconTintList = colorStateList
   589→                            bottomNav.itemTextColor = colorStateList
