package com.omiyawaki.osrswiki.network

import kotlinx.serialization.SerialName
import kotlinx.serialization.Serializable
import kotlinx.serialization.Transient

@Serializable
data class SearchApiResponse(
    val query: Query? = null
)

@Serializable
data class Query(
    val searchinfo: SearchInfo? = null,
    val search: List<SearchResult>? = null
)

@Serializable
data class SearchInfo(
    val totalhits: Int? = null
)

/**
 * Represents a single search result. This has been updated to match the structure
 * returned by the generator=prefixsearch API call.
 */
@Serializable
data class SearchResult(
    val ns: Int,
    val title: String,
    val pageid: Int,
    val index: Int, // The relevance index provided by the API for sorting.
    val size: Int? = null,
    val wordcount: Int? = null,
    // The new API returns 'extract' instead of 'snippet'.
    @SerialName("extract")
    val snippet: String? = null,
    val timestamp: String? = null,
    // The new API returns a nested 'thumbnail' object.
    val thumbnail: Thumbnail? = null,
    // This field is added locally, not from the network, so it must be marked @Transient.
    @Transient
    val isOfflineAvailable: Boolean = false
) {
    // Computed property to easily access the thumbnail URL.
    val thumbnailUrl: String?
        get() = thumbnail?.source
}

/**
 * Represents the nested thumbnail object in the API response.
 */
@Serializable
data class Thumbnail(
    val source: String? = null,
    val width: Int? = null,
    val height: Int? = null
)

package com.omiyawaki.osrswiki.network

import kotlinx.serialization.SerialName
import kotlinx.serialization.Serializable

/**
 * Data class to model the response for a prefixsearch query.
 * The structure is similar to SearchApiResponse but uses "prefixsearch" as the key.
 */
@Serializable
data class PrefixSearchApiResponse(
    val query: PrefixSearchQuery? = null
)

@Serializable
data class PrefixSearchQuery(
    // Use the annotation from kotlinx.serialization to match the JSON field name.
    @SerialName("prefixsearch")
    val prefixsearch: List<SearchResult>? = null
)

package com.omiyawaki.osrswiki.network.model

import com.omiyawaki.osrswiki.network.SearchResult
import kotlinx.serialization.SerialName
import kotlinx.serialization.Serializable

/**
 * Data class for the response from a generated prefix search.
 * The results are contained within a 'pages' array in the 'query' object.
 */
@Serializable
data class GeneratedSearchApiResponse(
    val query: QueryResult?
)

@Serializable
data class QueryResult(
    // The API returns 'pages' as an array, so the type must be a List, not a Map.
    @SerialName("pages")
    val pages: List<SearchResult>?
)

package com.omiyawaki.osrswiki.search

import android.util.Log
import androidx.paging.Pager
import androidx.paging.PagingConfig
import androidx.paging.PagingData
import com.omiyawaki.osrswiki.database.ArticleMetaDao
import com.omiyawaki.osrswiki.database.ArticleMetaEntity
import com.omiyawaki.osrswiki.database.OfflinePageFts
import com.omiyawaki.osrswiki.database.OfflinePageFtsDao
import com.omiyawaki.osrswiki.network.SearchResult
import com.omiyawaki.osrswiki.network.WikiApiService
import com.omiyawaki.osrswiki.search.db.RecentSearch
import com.omiyawaki.osrswiki.search.db.RecentSearchDao
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.flow.flow
import kotlinx.coroutines.flow.flowOf
import kotlinx.coroutines.flow.flowOn

private const val DEFAULT_SEARCH_RESULTS_PAGE_SIZE = 20
private const val TAG = "SearchRepository"

/**
 * Repository for fetching search results from the OSRS Wiki API and local databases.
 */
class SearchRepository(
    private val apiService: WikiApiService,
    private val articleMetaDao: ArticleMetaDao,
    private val offlinePageFtsDao: OfflinePageFtsDao,
    private val recentSearchDao: RecentSearchDao
) {

    // --- Recent Searches ---

    fun getRecentSearches(): Flow<List<RecentSearch>> {
        return recentSearchDao.getAll()
    }

    suspend fun insertRecentSearch(query: String) {
        val recentSearch = RecentSearch(query = query, timestamp = System.currentTimeMillis())
        recentSearchDao.insert(recentSearch)
    }

    suspend fun clearAllRecentSearches() {
        recentSearchDao.clearAll()
    }

    // --- Online and Offline Article Search ---

    fun getOnlineSearchResultStream(query: String): Flow<PagingData<SearchResult>> {
        Log.d(TAG, "getOnlineSearchResultStream called with query: $query")
        return Pager(
            config = PagingConfig(
                pageSize = DEFAULT_SEARCH_RESULTS_PAGE_SIZE,
                prefetchDistance = DEFAULT_SEARCH_RESULTS_PAGE_SIZE / 2,
                enablePlaceholders = false
            ),
            pagingSourceFactory = {
                SearchPagingSource(
                    apiService = apiService,
                    query = query,
                    articleMetaDao = articleMetaDao
                )
            }
        ).flow
    }

    fun searchOfflineArticlesByTitle(query: String): Flow<List<ArticleMetaEntity>> {
        val trimmedQuery = query.trim()
        Log.d(TAG, "searchOfflineArticlesByTitle called with query: $trimmedQuery")

        if (trimmedQuery.isEmpty()) {
            return flowOf(emptyList())
        }

        val searchQuery = "%$trimmedQuery%"
        return flow {
            try {
                emit(articleMetaDao.searchByTitle(searchQuery))
            } catch (e: Exception) {
                Log.e(TAG, "Error searching offline articles by title: ${e.message}", e)
                emit(emptyList())
            }
        }.flowOn(Dispatchers.IO)
    }

    fun searchOfflineFtsContent(query: String): Flow<List<OfflinePageFts>> {
        val trimmedQuery = query.trim()
        Log.d(TAG, "searchOfflineFtsContent called with query: $trimmedQuery")

        if (trimmedQuery.isEmpty()) {
            return flowOf(emptyList())
        }

        return flow {
            try {
                emit(offlinePageFtsDao.searchAll(trimmedQuery))
            } catch (e: Exception) {
                Log.e(TAG, "Error searching FTS offline content: ${e.message}", e)
                emit(emptyList())
            }
        }.flowOn(Dispatchers.IO)
    }
}

package com.omiyawaki.osrswiki.search

import android.util.Log
import androidx.paging.PagingSource
import androidx.paging.PagingState
import com.omiyawaki.osrswiki.database.ArticleMetaDao
import com.omiyawaki.osrswiki.network.SearchResult
import com.omiyawaki.osrswiki.network.WikiApiService
import retrofit2.HttpException
import java.io.IOException
import java.util.concurrent.CancellationException

private const val OSRS_WIKI_STARTING_PAGE_OFFSET = 0
private const val THUMBNAIL_SIZE = 240

class SearchPagingSource(
    private val apiService: WikiApiService,
    private val query: String,
    private val articleMetaDao: ArticleMetaDao
) : PagingSource<Int, SearchResult>() {

    override suspend fun load(params: LoadParams<Int>): LoadResult<Int, SearchResult> {
        val currentOffset = params.key ?: OSRS_WIKI_STARTING_PAGE_OFFSET
        val limit = params.loadSize

        if (query.isBlank()) {
            return LoadResult.Page(data = emptyList(), prevKey = null, nextKey = null)
        }

        return try {
            // Step 1: Make a single, combined network call.
            val response = apiService.generatedPrefixSearch(
                query = query,
                limit = limit,
                offset = currentOffset,
                thumbSize = THUMBNAIL_SIZE
            )

            val unsortedResults = response.query?.pages ?: emptyList()

            // Sort the results by the 'index' field to ensure correct relevance order.
            val searchResults = unsortedResults.sortedBy { it.index }

            if (searchResults.isEmpty()) {
                return LoadResult.Page(data = emptyList(), prevKey = null, nextKey = null)
            }

            // Step 2: Efficiently enhance results with offline status from the local database.
            val pageIds = searchResults.map { it.pageid }
            val offlineMetas = articleMetaDao.getMetasByPageIds(pageIds)
            val offlinePageIds = offlineMetas.map { it.pageId }.toSet()

            val enhancedSearchResults = searchResults.map { searchResult ->
                val isOffline = offlinePageIds.contains(searchResult.pageid)
                // Use .copy() to create a new instance with the offline status set.
                searchResult.copy(isOfflineAvailable = isOffline)
            }

            val nextKey = if (enhancedSearchResults.size < limit) null else currentOffset + limit
            val prevKey = if (currentOffset == OSRS_WIKI_STARTING_PAGE_OFFSET) null else (currentOffset - params.loadSize).coerceAtLeast(0)

            LoadResult.Page(
                data = enhancedSearchResults,
                prevKey = prevKey,
                nextKey = nextKey
            )
        } catch (exception: IOException) {
            Log.e("SearchPagingSource", "IOException during search", exception)
            return LoadResult.Error(exception)
        } catch (exception: HttpException) {
            Log.e("SearchPagingSource", "HttpException during search", exception)
            return LoadResult.Error(exception)
        } catch (exception: CancellationException) {
            // This is expected when a new search supersedes the current one.
            throw exception
        } catch (exception: Exception) {
            Log.e("SearchPagingSource", "Generic Exception during search", exception)
            return LoadResult.Error(exception)
        }
    }

    override fun getRefreshKey(state: PagingState<Int, SearchResult>): Int? {
        return state.anchorPosition?.let { anchorPosition ->
            val anchorPage = state.closestPageToPosition(anchorPosition)
            anchorPage?.prevKey?.plus(state.config.pageSize) ?: anchorPage?.nextKey?.minus(state.config.pageSize)
        }
    }
}

