     3→import android.view.LayoutInflater
     4→import android.view.View
     5→import android.view.ViewGroup
     6→import androidx.paging.PagingDataAdapter
     7→import androidx.recyclerview.widget.DiffUtil
     8→import androidx.recyclerview.widget.RecyclerView
     9→import com.bumptech.glide.Glide
    10→import com.bumptech.glide.load.resource.drawable.DrawableTransitionOptions
    11→import com.omiyawaki.osrswiki.databinding.ItemSearchResultBinding
    12→import com.omiyawaki.osrswiki.util.applyAlegreyaHeadline
    13→
    14→class SearchAdapter(
    15→    private val onItemClickListener: OnItemClickListener
    16→) : PagingDataAdapter<CleanedSearchResultItem, SearchAdapter.SearchResultViewHolder>(
    17→    SEARCH_RESULT_COMPARATOR
    18→) {
    19→
    20→    interface OnItemClickListener {
    21→        fun onItemClick(item: CleanedSearchResultItem)
    22→    }
    23→
    24→    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): SearchResultViewHolder {
    25→        val binding = ItemSearchResultBinding.inflate(LayoutInflater.from(parent.context), parent, false)
    26→        return SearchResultViewHolder(binding, onItemClickListener)
    27→    }
    28→
    29→    override fun onBindViewHolder(holder: SearchResultViewHolder, position: Int) {
    30→        val item = getItem(position)
    31→        if (item != null) {
    32→            holder.bind(item)
    33→        }
    34→    }
    35→
    36→    class SearchResultViewHolder(
    37→        private val binding: ItemSearchResultBinding,
    38→        private val listener: OnItemClickListener
    39→    ) : RecyclerView.ViewHolder(binding.root) {
    40→
    41→        fun bind(item: CleanedSearchResultItem) {
    42→            binding.searchItemTitle.text = item.title
    43→            binding.searchItemTitle.applyAlegreyaHeadline()
    44→
    45→            if (item.snippet.isNotBlank()) {
    46→                binding.searchItemSnippet.text = item.snippet
    47→                binding.searchItemSnippet.visibility = View.VISIBLE
    48→            } else {
    49→                binding.searchItemSnippet.visibility = View.GONE
    50→            }
    51→
    52→            // Load the thumbnail if the URL exists, otherwise hide the image view.
    53→            if (item.thumbnailUrl != null) {
    54→                binding.searchItemThumbnail.visibility = View.VISIBLE
    55→                Glide.with(binding.root.context)
    56→                    .load(item.thumbnailUrl)
    57→                    .transition(DrawableTransitionOptions.withCrossFade())
    58→                    .into(binding.searchItemThumbnail)
    59→            } else {
    60→                // It's important to clear the view and set visibility to GONE
    61→                // to handle view recycling correctly.
    62→                Glide.with(binding.root.context).clear(binding.searchItemThumbnail)
    63→                binding.searchItemThumbnail.visibility = View.GONE
    64→            }
    65→
    66→            binding.root.setOnClickListener {
    67→                listener.onItemClick(item)
    68→            }
    69→        }
    70→    }
    71→
