   289→    private fun applyNewBackgroundColor(recyclerView: RecyclerView) {
   290→        try {
   291→            val typedValue = android.util.TypedValue()
   292→            val theme = requireContext().theme
   293→            
   294→            // Try multiple background color attributes in order of preference
   295→            val backgroundAttrs = arrayOf(
   296→                com.omiyawaki.osrswiki.R.attr.paper_color,
   297→                com.google.android.material.R.attr.colorSurface,
   298→                android.R.attr.colorBackground
   299→            )
   300→            
   301→            for (attr in backgroundAttrs) {
   302→                if (theme.resolveAttribute(attr, typedValue, true)) {
   303→                    // Apply to RecyclerView
   304→                    recyclerView.setBackgroundColor(typedValue.data)
   305→                    
   306→                    // CRITICAL: Also apply to parent views to ensure complete background coverage
   307→                    applyBackgroundToParentViews(recyclerView, typedValue.data)
   308→                    
   309→                    L.d("AppearanceSettingsFragment: Applied background color from ${getAttributeName(attr)}: ${Integer.toHexString(typedValue.data)}")
   310→                    break
   311→                }
   312→            }
   313→        } catch (e: Exception) {
   314→            L.w("AppearanceSettingsFragment: Error applying background color: ${e.message}")
   315→        }
