   318→    private fun applyBackgroundToParentViews(view: android.view.View, backgroundColor: Int) {
   319→        try {
   320→            var currentView = view.parent
   321→            var level = 0
   322→            val maxLevels = 3 // Limit to prevent going too far up the hierarchy
   323→            
   324→            while (currentView != null && currentView is android.view.View && level < maxLevels) {
   325→                val parentView = currentView as android.view.View
   326→                
   327→                // Apply background to suitable parent views
   328→                if (shouldApplyBackgroundToParent(parentView)) {
   329→                    parentView.setBackgroundColor(backgroundColor)
   330→                    L.d("AppearanceSettingsFragment: Applied background to parent view level $level: ${parentView.javaClass.simpleName}")
   331→                }
   332→                
   333→                currentView = parentView.parent
   334→                level++
   335→            }
   336→        } catch (e: Exception) {
   337→            L.w("AppearanceSettingsFragment: Error applying background to parent views: ${e.message}")
   338→        }
   339→    }
   340→    
   341→    private fun shouldApplyBackgroundToParent(view: android.view.View): Boolean {
   342→        // Only apply background to certain types of parent views to avoid breaking other UI elements
   343→        val className = view.javaClass.simpleName
   344→        return className.contains("FrameLayout") || 
   345→               className.contains("LinearLayout") ||
   346→               className.contains("PreferenceRecyclerViewFragment") ||
   347→               view.id == android.R.id.list_container ||
   348→               view.id == androidx.preference.R.id.recycler_view
   349→    }
