   352→        try {
   353→            val typedValue = android.util.TypedValue()
   354→            val theme = requireContext().theme
   355→            
   356→            // Apply new theme background color to the fragment's root view
   357→            val backgroundAttrs = arrayOf(
   358→                com.omiyawaki.osrswiki.R.attr.paper_color,
   359→                com.google.android.material.R.attr.colorSurface,
   360→                android.R.attr.colorBackground
   361→            )
   362→            
   363→            for (attr in backgroundAttrs) {
   364→                if (theme.resolveAttribute(attr, typedValue, true)) {
   365→                    fragmentView.setBackgroundColor(typedValue.data)
   366→                    L.d("AppearanceSettingsFragment: Applied fragment view background: ${Integer.toHexString(typedValue.data)}")
   367→                    break
   368→                }
   369→            }
   370→            
   371→            // Force complete invalidation and redraw
   372→            fragmentView.invalidate()
   373→            fragmentView.requestLayout()
   374→            
   375→        } catch (e: Exception) {
   376→            L.w("AppearanceSettingsFragment: Error forcing fragment background refresh: ${e.message}")
   377→        }
   378→    }
