package com.omiyawaki.osrswiki.network

import kotlinx.serialization.SerialName
import kotlinx.serialization.Serializable
import kotlinx.serialization.Transient

@Serializable
data class SearchApiResponse(
    val query: Query? = null
)

@Serializable
data class Query(
    val searchinfo: SearchInfo? = null,
    val search: List<SearchResult>? = null
)

@Serializable
data class SearchInfo(
    val totalhits: Int? = null
)

/**
 * Represents a single search result. This has been updated to match the structure
 * returned by the generator=prefixsearch API call.
 */
@Serializable
data class SearchResult(
    val ns: Int,
    val title: String,
    val pageid: Int,
    val index: Int, // The relevance index provided by the API for sorting.
    val size: Int? = null,
    val wordcount: Int? = null,
    // The new API returns 'extract' instead of 'snippet'.
    @SerialName("extract")
    val snippet: String? = null,
    val timestamp: String? = null,
    // The new API returns a nested 'thumbnail' object.
    val thumbnail: Thumbnail? = null,
    // This field is added locally, not from the network, so it must be marked @Transient.
    @Transient
    val isOfflineAvailable: Boolean = false
) {
    // Computed property to easily access the thumbnail URL.
    val thumbnailUrl: String?
        get() = thumbnail?.source
}

/**
 * Represents the nested thumbnail object in the API response.
 */
@Serializable
data class Thumbnail(
    val source: String? = null,
    val width: Int? = null,
    val height: Int? = null
)

package com.omiyawaki.osrswiki.network.model

import com.omiyawaki.osrswiki.network.SearchResult
import kotlinx.serialization.SerialName
import kotlinx.serialization.Serializable

/**
 * Data class for the response from a generated prefix search.
 * The results are contained within a 'pages' array in the 'query' object.
 */
@Serializable
data class GeneratedSearchApiResponse(
    val query: QueryResult?
)

@Serializable
data class QueryResult(
    // The API returns 'pages' as an array, so the type must be a List, not a Map.
    @SerialName("pages")
    val pages: List<SearchResult>?
)

