public class TestMapScale { public static void main(String[] args) { System.out.println("MAP_SCALE value test"); int MAP_SCALE = 2; System.out.println("MAP_SCALE = " + MAP_SCALE); System.out.println("Pixels per tile = " + MAP_SCALE + " x " + MAP_SCALE + " = " + (MAP_SCALE * MAP_SCALE)); int totalPixels = 16384 * MAP_SCALE; System.out.println("Total image size = " + totalPixels + " x " + totalPixels + " pixels"); System.out.println("Memory reduction: 4x (from 4096x4096 to 2048x2048)"); } }
